INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (1,'MTP','Minimum Training Presence','The highest probability value at which 100% of input presence points remain classified as suitable habitat.',NULL,'Pearson, R. G., C. J. Raxworthy, M. Nakamura, and A. Townsend Peterson. 2007. Predicting species distributions from small numbers of occurrence records: a test case using cryptic geckos in Madagascar. Journal of Biogeography 34:102–117. ',3);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (2,'TenPctile','Tenth percentile of training presence','The probability at which 90% of the input presence points are classified as suitable habitat.',NULL,NULL,6);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (3,'maxSSS','Maximum of sensitivity plus specificity','The probability at which the sum of sensitivity and specificity is maximized.','LiuEtAl2015','Liu, C., G. Newell, and M. White. 2015. On the selection of thresholds for predicting species occurrence with presence‐only data. Ecology and Evolution 6:337–348.',2);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (4,'eqSS','Equal sensitivity and specificity','The probability at which the absolute value of the difference between sensitivity and specificity is minimized.','LiuEtAl2005','Liu, C., P. M. Berry, T. P. Dawson, and R. G. Pearson. 2005. Selecting thresholds of occurrence in the prediction of species distributions. Ecography 28:385–393.',1);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (5,'FMeasPt01','F-measure with alpha set to 0.01','The probability value at which the harmonic mean of precision and recall, with strong weighting towards recall, is maximized.','LiuEtAl2005','Liu, C., P. M. Berry, T. P. Dawson, and R. G. Pearson. 2005. Selecting thresholds of occurrence in the prediction of species distributions. Ecography 28:385–393.',7);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (6,'ROC','ROC plot upper left corner','The point on the ROC curve with the shortest distance to the top-left corner of the ROC plot.','LiuEtAl2005','Liu, C., P. M. Berry, T. P. Dawson, and R. G. Pearson. 2005. Selecting thresholds of occurrence in the prediction of species distributions. Ecography 28:385–393.',8);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (7,'MTPP','Minimum Training Presence by Polygon','The highest probability value at which 100% of input polygons have at least one presence point classified as suitable habitat.',NULL,'Pearson, R. G., C. J. Raxworthy, M. Nakamura, and A. Townsend Peterson. 2007. Predicting species distributions from small numbers of occurrence records: a test case using cryptic geckos in Madagascar. Journal of Biogeography 34:102–117. ',4);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (8,'MTPEO','Minimum Training Presence by Element Occurrence','The highest probability value at which 100% of input EOs have at least one presence point classified as suitable habitat.',NULL,'Pearson, R. G., C. J. Raxworthy, M. Nakamura, and A. Townsend Peterson. 2007. Predicting species distributions from small numbers of occurrence records: a test case using cryptic geckos in Madagascar. Journal of Biogeography 34:102–117. ',5);
INSERT INTO `lkpThresholdTypes` (ID,cutCode,cutFullName,cutDescription,cutCitationShort,cutCitationFull,sortOrder) VALUES (9,'MTPG','Minimum Training Presence by Group','The highest probability value at which 100% of input reach groups have at least one presence reach classified as suitable.',NULL,NULL,9);
