INSERT INTO `lkpEnvVars` (fullName,gridName,description,dataType,multiplier,category,comments,distToGrid,correlatedVarGroupings,use_T,use_K,use_B,use_S) VALUES ('Solar radiation equinox','radequinx','Total insolation derived from direct and diffuse, but not reflected, radiation for the equinox','continuous','1','Elevation and Derivatives','Team agreement that this variable could be useful. Team concurred to use all tool defaults. Fall and spring equinox values are equivalent, so only need one variable instead of two as previously thought.',0,62,0,0,0,0);
